  `created` datetime NOT NULL,
  `modified` datetime NOT NULL,
  `user_id` bigint(20) unsigned DEFAULT NULL,
  `city_id` bigint(20) unsigned DEFAULT NULL,
  `state_id` bigint(20) unsigned DEFAULT NULL,
  `country_id` bigint(20) unsigned DEFAULT NULL,
  `currency_id` bigint(20) unsigned DEFAULT NULL,
  `category_id` bigint(20) unsigned DEFAULT NULL,
  `category_type_id` bigint(20) unsigned DEFAULT NULL,
  `title` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `slug` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `description` text COLLATE utf8_unicode_ci,
  `address` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `accommodates` bigint(20) DEFAULT NULL,
  `latitude` float(10,6) DEFAULT NULL,
  `longitude` float(10,6) DEFAULT NULL,
  `zoom_level` int(11) DEFAULT '5',
  `from` date DEFAULT NULL,
  `to` date DEFAULT NULL,
  `price` double(10,2) NOT NULL,
  `is_active` tinyint(1) DEFAULT '1',
  `is_alert` tinyint(1) NOT NULL DEFAULT '0',
  `is_system_flagged` tinyint(1) NOT NULL DEFAULT '0',
  `is_user_flagged` tinyint(1) NOT NULL DEFAULT '0',
  `admin_suspend` tinyint(1) NOT NULL DEFAULT '0',
  `detected_suspicious_words` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `item_count` bigint(20) unsigned DEFAULT '0',
  `request_flag_count` bigint(20) NOT NULL DEFAULT '0',
  `request_view_count` bigint(20) unsigned NOT NULL DEFAULT '0',
  `request_favorite_count` bigint(20) DEFAULT '0',
  `is_approved` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`),
  KEY `city_id` (`city_id`),
  KEY `state_id` (`state_id`),
  KEY `country_id` (`country_id`),
  KEY `currency_id` (`currency_id`),
  KEY `slug` (`slug`),
  KEY `category_type_id` (`category_type_id`),
  KEY `category_id` (`category_id`),
  CONSTRAINT `requests_ibfk_2` FOREIGN KEY (`city_id`) REFERENCES `cities` (`id`) ON DELETE SET NULL,
  CONSTRAINT `requests_ibfk_3` FOREIGN KEY (`state_id`) REFERENCES `states` (`id`) ON DELETE SET NULL,
  CONSTRAINT `requests_ibfk_4` FOREIGN KEY (`country_id`) REFERENCES `countries` (`id`) ON DELETE SET NULL,
  CONSTRAINT `requests_ibfk_5` FOREIGN KEY (`currency_id`) REFERENCES `currencies` (`id`) ON DELETE SET NULL,
  CONSTRAINT `requests_ibfk_6` FOREIGN KEY (`category_id`) REFERENCES `categories` (`id`) ON DELETE SET NULL,
  CONSTRAINT `requests_ibfk_7` FOREIGN KEY (`category_type_id`) REFERENCES `category_types` (`id`) ON DELETE SET NULL,
  CONSTRAINT `requests_ibfk_8` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE SET NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci ROW_FORMAT=DYNAMIC;


